		TESTFW_EXPECTED( test_cmp( r.y.x, 3.76219569f ) );
		TESTFW_EXPECTED( test_cmp( r.z.x, 1.54308063f ) );
		TESTFW_EXPECTED( test_cmp( r.w.x, 1.00000000f ) );
