		TESTFW_EXPECTED( r.x.x == 0.25f );
		TESTFW_EXPECTED( r.y.x == -2.0f );
		TESTFW_EXPECTED( r.z.x == 0.5f );
		TESTFW_EXPECTED( r.w.x == 1.0f );
