		TESTFW_EXPECTED(test_cmp(t.x,5.0f)&&test_cmp(t.y,6.0f)&&test_cmp(t.z,7.0f));
		TESTFW_EXPECTED(test_cmp(q.x,expected_q.x)&&test_cmp(q.y,expected_q.y)&&test_cmp(q.z,expected_q.z)&&test_cmp(q.w,expected_q.w));
